svg.script:
var txt = document.getElementById("output");
var r = document.getElementById("rectangle");
var msg =  r.getAttribute("x") + ", " +
  r.getAttribute("y") + " " +
  r.style.getPropertyValue("stroke") + " " +
  r.style.getPropertyValue("fill"); 
r.setAttribute("height", "30");
txt.textContent= msg;
